  `summary` varchar(200) NOT NULL COMMENT '摘要',
  `raw_data` text NOT NULL COMMENT '原始源码',
  `trans_data` text NOT NULL COMMENT '处理后源码',
  `fetch_url` varchar(200) NOT NULL COMMENT '来源url',
  `is_recommend` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否被推荐到发现',
  `create_time` int(10) NOT NULL,
  PRIMARY KEY (`id`),
