CREATE TABLE IF NOT EXISTS `lxyd_data_catelog` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `data_id` int(10) NOT NULL,
  `catelog_id` int(10) NOT NULL,
  `user_id` int(10) NOT NULL COMMENT '用户id',
  PRIMARY KEY (`id`),
  KEY `data_id` (`data_id`,`catelog_id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COMMENT='用于发现的分类' AUTO_INCREMENT=3 ;

