="text/javascript" src="http://c.csdnimg.cn/rabbit/search-service/main.js"></script>\r\n<link rel="stylesheet" href="http://static.blog.csdn.net/public/res-min/markdown_views.css?v=1.0" />\r\n<link rel="stylesheet" href="http://static.blog.csdn.net/css/category.css?v=1.0" />\r\n<script type="text/javascript" src="http://static.blog.csdn.net/public/res/bower-libs/MathJax/MathJax.js?config=TeX-AMS_HTML"></script>\r\n<script type="text/javascript" src="http://static.blog.csdn.net/scripts/web-storage-cache.min.js"></script>\r\n<script type="text/javascript" src="http://static.blog.csdn.net/scripts/replace.min.js"></script>\r\n\r\n\r\n\r\n\r\n  <script type="text/ecmascript">\r\n      window.quickReplyflag = true;\r\n           \r\n            var isBole = false;\r\n            \r\n          \r\n    </script>\r\n<div id="article_details" class="details">\r\n    <div class="article_title">   \r\n         <span class="ico ico_type_Original"></span>\r\n\r\n\r\n    <h1>\r\n        <span class="link_title"><a href="/pleasecallmewhy/article/details/8592571">\r\n        [JavaScript]JS调用PHP和PHP调用JS的方法举例            \r\n        </a></span>\r\n    </h1>\r\n</div>\r\n\r\n   \r\n\r\n        <div class="article_manage clearfix">\r\n        <div class="article_r">\r\n            <span class="link_postdate">2013-02-19 21:53</span>\r\n            <span class="link_view" title="阅读次数">15782人阅读</span>\r\n            <span class="link_comments" title="评论次数"> <a href="#comments" onclick="_gaq.push([''_trackEvent'',''function'', ''onclick'', ''blog_articles_pinglun''])">评论</a>(0)</span>\r\n            <span class="link_collect tracking-ad" data-mod="popu_171"> <a href="javascript:void(0);" onclick="javascript:collectArticle(''[JavaScript]JS调用PHP和PHP调用JS的方法举例'',''8592571'');return false;" title="收藏">收藏</a></span>\r\n             <span class="link_report"> <a href="#report" onclick="javascript:report(8592571,2);return false;" title="举报">举报</a></span>\r\n\r\n        </div>\r\n    </div>\r\n    <div class="embody"  style="display:none" id="embody">\r\n        <span class="embody_t">本文章已收录于：</span>\r\n        <div class="embody_c" id="lib" value="{&quot;err&quot;:0,&quot;msg&quot;:&quot;ok&quot;,&quot;data&quot;:[]}"></div>\r\n    </div>\r\n    <style type="text/css">        \r\n            .embody{\r\n                padding:10px 10px 10px;\r\n                margin:0 -20px;\r\n                border-bottom:solid 1px #ededed;                \r\n            }\r\n            .embody_b{\r\n                margin:0 ;\r\n                padding:10px 0;\r\n            }\r\n            .embody .embody_t,.embody .embody_c{\r\n                display: inline-block;\r\n                margin-right:10px;\r\n            }\r\n            .embody_t{\r\n                font-size: 12px;\r\n                color:#999;\r\n            }\r\n            .embody_c{\r\n                font-size: 12px;\r\n            }\r\n            .embody_c img,.embody_c em{\r\n                display: inline-block;\r\n                vertical-align: middle;               \r\n            }\r\n             .embody_c img{               \r\n                width:30px;\r\n                height:30px;\r\n            }\r\n            .embody_c em{\r\n                margin: 0 20px 0 10px;\r\n                color:#333;\r\n                font-style: normal;\r\n            }\r\n    </style>\r\n    <script  type="text/javascript">\r\n        $(function () {\r\n            try\r\n            {\r\n                var lib = eval("("+$("#lib").attr("value")+")");\r\n                var html = "";\r\n                if (lib.err == 0) {\r\n                    $.each(lib.data, function (i) {\r\n                        var obj = lib.data[i];\r\n                        //html += ''<img src="'' + obj.logo + ''"/>'' + obj.name + "&nbsp;&nbsp;";\r\n                        html += '' <a href="'' + obj.url + ''" target="_blank">'';\r\n                        html += '' <img src="'' + obj.logo + ''">'';\r\n                        html += '' <em><b>'' + obj.name + ''</b></em>'';\r\n                        html += '' </a>'';\r\n                    });\r\n                    if (html != "") {\r\n                        setTimeout(function () {\r\n                            $("#lib").html(html);                      \r\n                            $("#embody").show();\r\n                        }, 100);\r\n                    }\r\n                }      \r\n            } catch (err)\r\n            { }\r\n            \r\n        });\r\n    </script>\r\n      <div class="category clearfix">\r\n        <div class="category_l">\r\n           <img src="http://static.blog.csdn.net/images/category_icon.jpg">\r\n            <span>分类：</span>\r\n        </div>\r\n        <div class="category_r">\r\n                    <label  onclick="GetCategoryArticles(''1335773'',''wxg694175346'',''top'',''8592571'');">\r\n                        <span onclick="_gaq.push([''_trackEvent'',''function'', ''onclick'', ''blog_articles_fenlei'']);">JavaScript<em>（8）</em></span>\r\n                      <img class="arrow-down" src="http://static.blog.csdn.net/images/arrow_triangle _down.jpg" style="display:inline;">\r\n                      <img class="arrow-up" src="http://static.blog.csdn.net/images/arrow_triangle_up.jpg" style="display:none;">\r\n                        <div class="subItem">\r\n                            <div class="subItem_t"><a  href="http://blog.csdn.net/wxg694175346/article/category/1335773"  target="_blank">作者同类文章</a><i class="J_close">X</i></div>\r\n                            <ul class="subItem_l" id="top_1335773">                            \r\n                            </ul>\r\n                        </div>\r\n                    </label>                    \r\n                    <label  onclick="GetCategoryArticles(''1341271'',''wxg694175346'',''top'',''8592571'');">\r\n                        <span onclick="_gaq.push([''_trackEvent'',''function'', ''onclick'', ''blog_articles_fenlei'']);">PHP<em>（30）</em></span>\r\n                      <img class="arrow-down" src="http://static.blog.csdn.net/images/arrow_triangle _down.jpg" style="display:inline;">\r\n                      <img class="arrow-up" src="http://static.blog.csdn.net/images/arrow_triangle_up.jpg" style="display:none;">\r\n                        <div class="subItem">\r\n                            <div class="subItem_t"><a  href="http://blog.csdn.net/wxg694175346/article/category/1341271"  target="_blank">作者同类文章</a><i class="J_close">X</i></div>\r\n                            <ul class="subItem_l" id="top_1341271">                            \r\n                            </ul>\r\n                        </div>\r\n                    </label>                    \r\n        </div>\r\n    </div>\r\n    <script   type="text/javascript" src="http://static.blog.csdn.net/scripts/category.js"></script>  \r\n        <div   class="bog_copyright">         \r\n            <p  class="copyright_p" >版权声明：本文为博主原创文章，未经博主允许不得转载。</p>\r\n        </div>\r\n\r\n  \r\n\r\n  \r\n  \r\n     \r\n\r\n<div id="article_content" class="article_content">\r\n\r\n<span style="font-family:Microsoft YaHei; font-size:18px">1<span style="font-size:18px">、</span>JS方式调用PHP文件并取得<span style="font-family:Microsoft YaHei; font-size:18px">PHP</span>中的&#20540;<br>\r\n举一个简单的例子来说明:<br>\r\n如在页面test_json1<span style="font-size:18px">中</span>用下面这句调用:<br>\r\n</span>\r\n<p></p>\r\n<pre name="code" class="html">&lt;script type=&quot;text/javascript&quot; src=&quot;http://callmewhy.sinaapp.com/index.php/test/testjson2&quot;&gt;\n&lt;/script&gt;\n&lt;script type=&quot;text/javascript&quot; &gt;\n    alert(jstext);\n&lt;/script&gt;\n\n</pre><span style="font-family:Microsoft YaHei; font-size:18px">在<span style="font-size:18px">test_json<span style="font-size:18px">2</span></span>.php中有这样一段PHP代码:</span>\r\n<p></p>\r\n<span style="font-family:Microsoft YaHei; font-size:18px"></span><pre name="code" class="php">&lt;?php\n$php_test=''I come from PHP!'';\necho &quot;var test=''$php_test'';&quot;;\necho &quot;var jstext=&quot;.&quot;''$php_test'';&quot;; \n?&gt; </pre><span style="font-family:Microsoft YaHei; font-size:18px">当执行<span style="font-family:Microsoft YaHei; font-size:18px"><span style="font-size:18px">test_json<span style="font-size:18px">1</span></span></span>.php文件时,就会调用<span style="font-family:Microsoft YaHei; font-size:18px"><span style="font-size:18px">test_json<span style="font-size:18px">2</span></span></span>.php文件,并将b.php文件的输出作为JS语句来执行,</span>\r\n<p><span style="font-family:Microsoft YaHei; font-size:18px">所以此处会弹出一个提示框,内容为JS变量jstext的&#20540;,也就是在PHP文件中赋给jstext的&#20540;.</span></p>\r\n<span style="font-family:Microsoft YaHei; font-size:18px"><br>\r\n小结:在HTML里用JS 调用文件的方式调PHP文件,则PHP文件的输出将会被调用页作为JS的代码来用.<br>\r\n<br>\r\n2<span style="font-size:18px"><span style="font-size:18px">、</span>PHP</span>调用<span style="font-size:18px">JS</span>中的&#20540;<br>\r\n<br>\r\n在test_js<span style="font-size:18px"><span style="font-size:18px">o</span>n3</span>.php页面中有这样一段代码：<br>\r\n</span><pre name="code" class="html">&lt;script type=&quot;text/javascript&quot; &gt; \nvar data=&quot;call_me_why&quot;; \n&lt;/script&gt;\n&lt;? \n	echo &quot;&lt;script type=text/javascript&gt;document.write(data)&lt;/script&gt;&quot;;\n?&gt;</pre><br>\r\n<span style="font-family:Microsoft YaHei; font-size:18px">3、php调用js中的方法(函数)<br>\r\n和第二种情况基本类&#20284;，利用echo脚本来实现JS调用</span><br>\r\n<pre name="code" class="html">&lt;script type=&quot;text/javascript&quot;&gt; \nfunction test() {   \n	var t1=3;   \n	t1 = t1+2;   \n	alert(t1);   \n	//return t1; \n} \n&lt;/script&gt;\n&lt;?php echo &quot;&lt;script type=''text/javascript''&gt;test();&lt;/script&gt;&quot;; ?&gt;\n\n </pre><br>\r\n<br>\r\n<br>\r\n<span style="font-family:Microsoft YaHei; font-size:18px">4、JS调用PHP变量<br>\r\n（1）</span><br>\r\n<pre name="code" class="html">&lt;?php\n	$userId=100;\n?&gt; \n&lt;script&gt;\n	var userId;\n	userId=document.getElementById(&quot;userId&quot;).value;\n	alert(userId);\n&lt;/script&gt;\n&lt;input type=&quot;text&quot; name=&quot;userId&quot; id=&quot;userId&quot; value=&quot;&lt;?php echo $userId; ?&gt;&quot;&gt;</pre><br>\r\n<span style="font-family:Microsoft YaHei; font-size:18px">（2）</span><br>\r\n<pre name="code" class="php">&lt;?php\n    $url = ''CALL_ME_WHY'';    //定义变量\n?&gt;\n&lt;script type=&quot;text/javascript&quot;&gt;\n    //js调用php变量\n    var ds =&quot;&lt;?php echo $url?&gt;&quot; ; //赋值\n    alert(ds); //输出效果\n&lt;/script&gt;</pre><br>\r\n<br>\r\n<span style="font-family:Microsoft YaHei; font-size:18px">5 、</span><span style="font-family:Microsoft YaHei; font-size:18px"><span style="font-size:18px">JS调用PHP函数</span><br>\r\n</span><pre name="code" class="html">&lt;script language=&quot;JavaScript&quot;&gt;\n	var Y=&lt;?php echo date(''Y'')?&gt;,M=&lt;?php echo date(''n'')?&gt;,D=&lt;?php echo date(''j'')?&gt;;\n	alert(Y);\n	alert(M);\n	alert(D);\n&lt;/script&gt;</pre><br>\r\n<br>\r\n   \r\n</div>\r\n\r\n\r\n\r\n\r\n<!-- Baidu Button BEGIN -->\r\n\r\n\r\n\r\n\r\n<div class="bdsharebuttonbox tracking-ad" style="float: right;" data-mod="popu_172">\r\n<a href="#" class="bds_more" data-cmd="more" style="background-position:0 0 !important; background-image: url(http://bdimg.share.baidu.com/static/api/img/share/icons_0_16.png?v=d754dcc0.png) !important"></a>\r\n<a href="#" class="bds_qzone" data-cmd="qzone" title="分享到QQ空间"  style="background-position:0 -52px !important"></a>\r\n<a href="#" class="bds_tsina" data-cmd="tsina" title="分享到新浪微博"style="background-position:0 -104px !important"></a>\r\n<a href="#" class="bds_tqq" data-cmd="tqq" title="分享到腾讯微博"style="background-position:0 -260px !important"></a>\r\n<a href="#" class="bds_renren" data-cmd="renren" title="分享到人人网"style="background-position:0 -208px !important"></a>\r\n<a href="#" class="bds_weixin" data-cmd="weixin" title="分享到微信"style="background-position:0 -1612px !important" ></a>\r\n</div>\n<script>window._bd_share_config = { "commo