INSERT INTO `lxyd_data` (`id`, `catelog_id`, `user_id`, `title`, `summary`, `raw_data`, `trans_data`, `fetch_url`, `platform`, `create_time`) VALUES
(9, 0, 1024, '极验验证：传统验证码破解算法浅析', '\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n摘要：哈喽小伙伴们~大家都知道，极验验证是首创了行为式的验证技术，以滑动的验证形式取代了传统验证码识别的验证形式。极验首席砖家也一直告诉大家，传统的验证码很容易', '<!DOCTYPE html PUBLIC \\"-//W3C//DTD XHTML 1.0 Transitional//EN\\" \\"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\\">\n<html xmlns=\\"http://www.w3.org/1999/xhtml\\">\n<head>\n<link href=\\"http://c.csdnimg.cn/public/favicon.ico\\" rel=\\"SHORTCUT ICON\\" />\n<meta http-equiv=\\"Content-Type\\" content=\\"text/html; charset=utf-8\\" />\n	\n<title>极验验证：传统验证码破解算法浅析-CSDN.NET </title>\n<meta name=\\"description\\" content=\\"哈喽小伙伴们~大家都知道，极验验证是首创了行为式的验证技术，以滑动的验证形式取代了传统验证码识别的验证形式。极验首席砖家也一直告诉大家，传统的验证码很容易被破解，形同虚设。但是大家肯定也有疑问，为什么说传统验证码容易被破解呢?那么今天极验破解砖家就带大家了解一下传统验证码的破解原理。\\" />\n<meta name=\\"keywords\\" content=\\"\\" />\n<meta name=\\"author\\" content=\\"CSDN.NET\\" />\n<meta name=\\"Copyright\\" content=\\"CSDN.NET\\" />\n<meta name=\\"image\\" content=\\"\\" />\n<!-- toolbar start --> \n<link href=\\"http://c.csdnimg.cn/public/common/toolbar/css/index.css\\" type=\\"text/css\\" rel=\\"stylesheet\\">\n<script src=\\"http://c.csdnimg.cn/public/common/libs/jquery/jquery-1.9.1.min.js\\" type=\\"text/javascript\\"></script>\n<!-- toolbar end -->\n<link href=\\"http://news.csdn.net/css/content.css\\" rel=\\"stylesheet\\" type=\\"text/css\\" />\n<script type=\\"text/javascript\\">\n\nvar _hmt = _hmt || [];\n\n(function() {\n\n  var hm = document.createElement(\\"script\\");\n\n  hm.src = \\"//hm.baidu.com/hm.js?ba776f11bc6ba54ecefb321520244a3c\\";\n\n  var s = document.getElementsByTagName(\\"script\\")[0];\n\n  s.parentNode.insertBefore(hm, s);\n\n})();\n</script>\n<script src=\\"http://ads.csdn.net/js/tracking.js\\" type=\\"text/javascript\\"></script>\n</head>\n<body>\n<script type=\\"text/javascript\\" ads-src=\\"http://csdnim.qtmojo.com/main/s?user=allyestest|allyestest|allyestest0410&db=csdnim&border=0&local=yes&js=ie\\" src=\\"http://creatim.qtmojo.cn/td/AllyesDeliver.min.js\\" charset=\\"gbk\\"></script>