INSERT INTO `lxyd_data` (`id`, `catelog_id`, `user_id`, `title`, `summary`, `raw_data`, `trans_data`, `fetch_url`, `platform`, `create_time`) VALUES
(14, 0, 1024, '油价迎来下半年首次上调 加满一箱油多花7块钱', '\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n　　权威发布！今夜以后，加满一箱油将多花7块钱\r\n　　(央视财经讯 记者 平凡\r\n于小曼)我国年内第16次成品油零售价调价窗口于8月18日24时开启。受国际油价持续', '<!DOCTYPE html>\n<!-- [ published at 2016-08-18 17:27:43 ] -->\n\n<html>\n<head>\n    <link rel=\\"mask-icon\\" sizes=\\"any\\" href=\\"http://www.sina.com.cn/favicon.svg\\" color=\\"red\\">\n	<meta http-equiv=\\"X-UA-Compatible\\" content=\\"IE=edge\\" />\n	<meta charset=\\"utf-8\\"/>\n<meta name=\\"sudameta\\" content=\\"urlpath:chanjing/cyxw/; allCIDs:56425,257,51894,56418,76670,258,51070,76478\\">\n<title>油价迎来下半年首次上调 加满一箱油多花7块钱|油价上调|成品油|国际油价_新浪财经_新浪网</title>\n<meta name=\\"keywords\\" content=\\"油价上调,成品油,国际油价\\" />\n<meta name=\\"tags\\" content=\\"油价上调,成品油,国际油价\\" />\n<meta name=\\"description\\" content=\\"权威发布！今夜以后，加满一箱油将多花7块钱(央视财经讯记者平凡于小曼)我国年内第16次成品油零售价调价窗口于8月18日24时开启。受国际油价持续上涨影响，国家发改委宣布，本次成品油价格将迎来下半年首次上调，汽油每吨上调175元，柴油每吨上调\\" />\n\n<meta property=\\"og:type\\" content=\\"news\\" />\n<meta property=\\"og:title\\" content=\\"油价迎来下半年首次上调 加满一箱油多花7块钱\\" />\n<meta property=\\"og:description\\" content=\\"油价迎来下半年首次上调 加满一箱油多花7块钱\\" />\n<meta property=\\"og:url\\" content=\\"http://finance.sina.com.cn/chanjing/cyxw/2016-08-18/doc-ifxvcsrn8589194.shtml\\" />\n<meta property=\\"og:image\\" content=\\"http://n.sinaimg.cn/finance/transform/20160818/jsUS-fxvcsrm1818471.jpg\\" />\n<meta name=\\"weibo: article:create_at\\" content=\\"2016-08-18 16:58:40\\" />\n<meta name=\\"weibo: article:update_at\\" content=\\"2016-08-18 17:27:42\\" />\n\n<!--news_web_article_v2014_meta -->\n\n\n<meta name=\\"stencil\\" content=\\"PGLS000526\\" />\n<meta name=\\"publishid\\" content=\\"fxvcsrn8589194\\" />\n<meta name=\\"comment\\" content=\\"cj:comos-fxvcsrn8589194\\" />\n<meta name=\\"sudameta\\" content=\\"comment_channel:cj;comment_id:comos-fxvcsrn8589194\\" />\n\n\n\n<meta name=\\"mediaid\\" content=\\"央视财经频道\\">\n<meta name=\\"sudameta\\" content=\\"sinaog:0\\" />\n<!-- web_article_v2015_mobile_agent_meta begin-->\n<me